static VALUE
rb_some_text_width(VALUE text, float default_width, float char_widths[127], float kernings[127][127]) {
  char *text_value = StringValueCStr(text);

  double total = 0;
  char ch = 0;
  char nch = 0;
  int i = 0;

  for (i = 0; i < strlen(text_value); i++) {
    ch = text_value[i];
    nch = text_value[i + 1];

    if (ch < 32) continue;

    if (ch > 126) {
      total += default_width;
    } else if (kernings[ch][nch]) {
      total += kernings[ch][nch];
      i++;
    } else {
      total += char_widths[ch];
    }
  }

  return DBL2NUM(total);
}

static VALUE
rb_arial_text_width(VALUE self, VALUE text) {
  return rb_some_text_width(text, ARIAL_DEFAULT, ARIAL_CHAR_WIDTH, ARIAL_KERNING);
}

static VALUE
rb_calibri_text_width(VALUE self, VALUE text) {
  return rb_some_text_width(text, CALIBRI_DEFAULT, CALIBRI_CHAR_WIDTH, CALIBRI_KERNING);
}

static VALUE
rb_times_new_roman_text_width(VALUE self, VALUE text) {
  return rb_some_text_width(text, TIMES_NEW_ROMAN_DEFAULT, TIMES_NEW_ROMAN_CHAR_WIDTH, TIMES_NEW_ROMAN_KERNING);
}

void Init_text_width_ext()
{
  VALUE fastExcel = rb_define_module("FastExcel");
  rb_define_singleton_method(fastExcel, "arial_text_width", rb_arial_text_width, 1);
  rb_define_singleton_method(fastExcel, "calibri_text_width", rb_calibri_text_width, 1);
  rb_define_singleton_method(fastExcel, "times_new_roman_text_width", rb_times_new_roman_text_width, 1);
}